<span class="line"><span style="color:#A6ACCD;">init_connect</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">SET NAMES utf8</span><span style="color:#89DDFF;">&#39;</span></span>
